import React, { useState } from 'react';
import './App.css';
import TasksTable from '../tasksTable/TasksTable';

// rsc

function App() {
    const [tasks, setTasks] = useState([
        {
            id: 1,
            taskName: 'some Name',
            pointsType: 'hours',
            currentPointsValue: 1,
            levelUpLimit: 5,
            levelUpType: ['Junipero', 'Midelero', 'Senioro'],
            currentLevel: 0
        },
        {
            id: 2,
            taskName: 'exact Name',
            pointsType: 'points',
            currentPointsValue: 5,
            levelUpLimit: 10,
            levelUpType: ['salobon', 'master'],
            currentLevel: 0
        }
    ]);
    const [task, setTask] = useState({
        taskName: '',
        pointsType: '',
        levelUpType: [],
        levelUpLimit: 0
    });
    const [taskName, setTaskName] = useState('');
    const [pointsType,setPointsType] = useState('Choose points type');
    const [levelUpType, setLevelUpType] = useState('Choose level up type');
    const [levelUpLimit, setLevelUpLimit] = useState('');

    const onAddTaskBtnClick = (e) => {
        e.preventDefault();

        const newTask = {
            id: Date.now(),
            taskName,
            pointsType,
            currentPointsValue: 0,
            levelUpLimit,
            levelUpType,
            currentLevel: 0

        }
        setTasks([...tasks, newTask]);
        setTaskName('');
        setLevelUpLimit('');
    }

    const levelUpTypeCheck = (levelUpType) => {
        const numbersArr = [];

        for (let i = 0; i <= 100; i++) {
            numbersArr.push(i);
        }

        const levelUpTypes = {
            it: ['Trainee', 'Junior', 'Middle', 'Senior'],
            numbersCounter: numbersArr,
            defineOwnType: ['own type']
        };

        switch (levelUpType) {
            case 'it':
                return levelUpTypes.it;
            case 'numbersCounter':
                return levelUpTypes.numbersCounter;
            case 'ownType':
                return levelUpTypes.defineOwnType;
        }
    }

    return (
        <div className="App">
            <form>
                <div>
                    <input
                        onChange={ e => setTaskName({ ...task, taskName: e.target.value }) }
                        value={ task.taskName }
                        required
                        type="text"
                        placeholder="Task name"
                    />
                </div>
                <div>
                    <select
                        required
                        size="1"
                        name="pointsType"
                        onChange={ e => setPointsType({ ...task, pointsType: e.target.value }) }
                        value={ task.pointsType }
                    >
                        <option disabled>Choose points type</option>
                        <option value="hours">hours</option>
                        <option value="points">points</option>
                        <option value="counter">counter</option>
                    </select>
                </div>
                <div>
                    <select
                        required
                        size="1"
                        name="levelUpType"
                        onChange={ e => setLevelUpType({ ...task, levelUpType: levelUpTypeCheck(e.target.value) }) }
                        value={ task.levelUpType }
                    >
                        <option disabled>Choose level up type</option>
                        <option value="it">Trainee, Junior, Middle, Senior</option>
                        <option value="numbersCounter">Numbers counter</option>
                        <option value="ownType">Define own type</option>
                    </select>
                </div>
                <div>
                    <input
                        onChange={ e => setLevelUpLimit({ ...task, levelUpLimit: Number(e.target.value) }) }
                        value={ task.levelUpLimit }
                        required type="text"
                        placeholder="Set up level up limit"
                    />
                </div>
                <div>
                    <button  className="addTaskBtn" onClick={ onAddTaskBtnClick }>Add task</button>
                </div>

            </form>

            <TasksTable tasks={ tasks }/>
            <p>how much time did you spent today?</p>
            <p>how much time did you spent for the last week?</p>
            <h3>React</h3>
            <p>? как передавать данные через один дочерний компонент</p>
            <p>? как тригерить событие родительского компонента из дочернего</p>
            <p>? как передать данные из дочернего компонента в родительский</p>
            <p>? чем отличается сет стейт от стейта, когда какой лучше юзать</p>
        </div>
    );
}

export default App;
