import React, { useState } from 'react';

function Task({ task }) {
    const [pointsDone, setPointsDone] = useState(task.currentPointsValue);
    const [currentLevel, setCurrentLevel] = useState(task.currentLevel);

    function increment() {

        if (pointsDone === task.levelUpLimit) {
            setPointsDone(0);
            setCurrentLevel(currentLevel !== task.levelUpType.length ? currentLevel + 1 : currentLevel);

            return;
        }

        setPointsDone(pointsDone + 1);
    }

    function decrement() {
        setPointsDone(pointsDone === 1 ? pointsDone : pointsDone - 1);
    }

    return (
        <tr className="tasks-table__row">
            <td className="tasks-table__cell">{ task.taskName }</td>
            <td className="tasks-table__cell">
                <span className="tasks-table__cell-points-value">
                    { pointsDone } / { task.levelUpLimit }
                </span>
                <button
                    onClick={ increment }
                    className="tasks-table__cell-button"
                >+</button>
                <button
                    onClick={ decrement }
                    className="tasks-table__cell-button"
                >-</button>
            </td>
            <td className="tasks-table__cell">{ task.levelUpType[currentLevel] }</td>
            <td className="tasks-table__cell">
                <button>Edit</button>
                <button>Delete</button>
            </td>
        </tr>
    );
}
