import React, {
    useMemo,
    useState,
    useEffect
} from 'react';
import './App.css';
import axios from 'axios';
import TasksTable from '../tasksTable/TasksTable.jsx';
import AddTaskForm from '../addTaskForm/AddTaskForm';
import ButtonDefault from '../UI/button/ButtonDefault';
import SearchTaskForm from '../searchTaskForm/SearchTaskForm';
import Modal from '../modal/Modal';

function App() {
    useEffect(() => {
        const isDataAtStorage = localStorage.getItem('tasks');

        isDataAtStorage ? setTasks(JSON.parse(localStorage.getItem('tasks'))) : localStorage.setItem('tasks', JSON.stringify([]));
            // const tasksData = JSON.parse(localStorage.getItem('tasks'));
            //
            // if (tasksData.length > 0) {
            //     setTasks(tasksData);
            // }
            // setTasks(JSON.parse(localStorage.getItem('tasks')));
        // }
    }, []);

    const [tasks, setTasks] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [modal, setModal] = useState(false);
    const createTask = (task) => {
        const newTasksArr = [...tasks, task];

        setTasks(newTasksArr);
        setModal(false);
        localStorage.setItem('tasks', JSON.stringify(newTasksArr));
        console.log(JSON.parse(localStorage.getItem('tasks')));
    }

    // const saveDataToLocalStorage = (newTask) => {
    //     const dataAtStorage = fetchTasks();
    //
    //     dataAtStorage.push(newTask);
    //     localStorage.setItem('tasks', JSON.stringify(dataAtStorage));
    //     console.log(JSON.parse(localStorage.getItem('tasks')));
    // }
    const removeTask = (taskId) => {
        const newTasksArr = tasks.filter(task => task.id !== taskId);

        setTasks(newTasksArr);
        localStorage.setItem('tasks', JSON.stringify(newTasksArr));
    }
    const searchQueryCallback = (taskNameToSearch) => setSearchQuery(taskNameToSearch);
    const fetchTasks = () => JSON.parse(localStorage.getItem('tasks'));

    const foundTasks = useMemo(() => {
        if (searchQuery) {
            let regexp = new RegExp(searchQuery, 'gi');

            return tasks.filter((tasksItem) => regexp.test(tasksItem.taskName));
        }

        return tasks;
    }, [searchQuery, tasks]);

    return (
        <div className="app">
            <button onClick={ fetchTasks }>Get tasks</button>
            <Modal visible={ modal } setVisible={ setModal } >
                <AddTaskForm create={ createTask } />
            </Modal>
            <div className="app__heading-actions-container">
                <ButtonDefault onClick={ () => setModal(true) } >Create task</ButtonDefault>
                <SearchTaskForm searchTask={ searchQueryCallback } />
            </div>
            <TasksTable tasks={ foundTasks } removeTask={ removeTask } />

            <p>how much time did you spent today?</p>
            <p>how much time did you spent for the last week?</p>
            <h3>React</h3>
            <p>? чем отличается хук сет стейт от стейта классового компонента, когда какой лучше юзать</p>
        </div>
    );
}

export default App;
