import React, { useMemo, useState } from 'react';
import InputDefault from '../UI/input/InputDefault';
import ButtonDefault from '../UI/button/ButtonDefault';
import './SearchTaskForm.css';

const SearchTaskForm = ({ searchTask }) => {
    const [searchTaskName, setSearchTaskName] = useState('');
    const onSearchBtnClick = (e) => {
        e.preventDefault();
        searchTask(searchTaskName);
        setSearchTaskName('');
    }

    return (
        <form className="search-task-form">
            <InputDefault
                onChange={ e => setSearchTaskName(e.target.value) }
                required type="text"
                placeholder="Task name"
                value={ searchTaskName }
            />
            <ButtonDefault
                style={{ marginBottom: '20px' }}
                onClick={ onSearchBtnClick }
            >Search task</ButtonDefault>
        </form>
    );
};

export default SearchTaskForm;
