import React, { useState } from 'react';
import './App.css';
import TasksTable from '../tasksTable/TasksTable.jsx';
import TaskForm from '../taskForm/TaskForm';

// rsc

function App() {
    const [tasks, setTasks] = useState([]);
    const createTask = (task) => setTasks([...tasks, task]);
    const removeTask = (taskId) => setTasks(tasks.filter(task => task.id !== taskId));

    return (
        <div className="App">
            <TaskForm create={ createTask } />
            <TasksTable tasks={ tasks } removeTask={ removeTask } />

            <p>how much time did you spent today?</p>
            <p>how much time did you spent for the last week?</p>
            <h3>React</h3>
            <p>? как передавать данные через один дочерний компонент</p>
            <p>? как тригерить событие родительского компонента из дочернего</p>
            <p><s>? как передать данные из дочернего компонента в родительский</s> = через callback в пропсах</p>
            <p>? чем отличается сет стейт от стейта, когда какой лучше юзать</p>
        </div>
    );
}

export default App;
