import React from 'react';
import './TasksTable.css';
import Task from '../task/Task.jsx';
import {
    CSSTransition,
    TransitionGroup,
} from 'react-transition-group';

function TasksTable({ tasks, removeTask, completeTask, updateTask }) {
    const noTasks = tasks.length === 0;

    return (
        <div className="tasks-table-container">
            <table className="tasks-table">
                <thead>
                    <tr>
                        <th className="tasks-table__cell">#</th>
                        <th className="tasks-table__cell">Task</th>
                        <th className="tasks-table__cell">Points done</th>
                        <th className="tasks-table__cell">Level</th>
                        <th className="tasks-table__cell">Edit</th>
                    </tr>
                </thead>
                <tbody>
                    <TransitionGroup component={ null }>
                        { tasks.map((task, index) => {
                            return (
                                <CSSTransition
                                    key={ task.id }
                                    timeout={ 500 }
                                    classNames="task-item"
                                >
                                    <Task
                                        num={ index + 1 }
                                        task={ task }
                                        removeTask={ removeTask }
                                        completeTask={ completeTask }
                                        updateTask={ updateTask }
                                    />
                                </CSSTransition>)
                        }) }
                    </TransitionGroup>
                </tbody>
            </table>
            { noTasks && <p className="no-tasks-msg">No tasks found</p> }
        </div>
    );
}

export default TasksTable;
