import React, { useState } from 'react';
import './App.css';
import TasksTable from '../tasksTable/TasksTable';

// rsc

function App() {
    const [tasks, setTasks] = useState([]);
    const [task, setTask] = useState({
        taskName: '',
        pointsType: 'Choose points type',
        currentPointsValue: 0,
        levelUpType: ['Choose level up type'],
        levelUpLimit: '',
        currentLevel: 0
    });

    const onAddTaskBtnClick = (e) => {
        e.preventDefault();
        setTasks([...tasks, { ...task, id: Date.now() }]);
        setTask({
            taskName: '',
            pointsType: 'Choose points type',
            currentPointsValue: 0,
            levelUpType: 'Choose level up type',
            levelUpLimit: '',
            currentLevel: 0
        });
    }

    const levelUpTypeCheck = (levelUpType) => {
        switch (levelUpType) {
            case 'it':
                return ['Trainee', 'Junior', 'Middle', 'Senior'];
            case 'cabaliero':
                return ['Junipero', 'Midelero', 'Senioro'];
            case 'shaolin':
                return ['Salobon', 'Master'];
            case 'numbersCounter':
                const numbersArr = [];

                for (let i = 0; i <= 100; i++) {
                    numbersArr.push(i);
                }

                return numbersArr;
            case 'ownType':
                return ['own type'];
        }
    }

    return (
        <div className="App">
            <form>
                <div>
                    <input
                        onChange={ e => setTask({ ...task, taskName: e.target.value }) }
                        value={ task.taskName }
                        required
                        type="text"
                        placeholder="Task name"
                    />
                </div>
                <div>
                    <select
                        required
                        size="1"
                        name="pointsType"
                        onChange={ e => setTask({ ...task, pointsType: e.target.value }) }
                        value={ task.pointsType }
                    >
                        <option disabled>Choose points type</option>
                        <option value="hours">hours</option>
                        <option value="points">points</option>
                        <option value="counter">counter</option>
                    </select>
                </div>
                <div>
                    <select
                        required
                        size="1"
                        name="levelUpType"
                        onChange={ e => setTask({ ...task, levelUpType: levelUpTypeCheck(e.target.value) }) }
                        value={ task.levelUpType }
                    >
                        <option disabled>Choose level up type</option>
                        <option value="it">Trainee, Junior, Middle, Senior</option>
                        <option value="cabaliero">Junipero, Midelero, Senioro</option>
                        <option value="shaolin">Salobon, Master</option>
                        <option value="numbersCounter">Numbers counter</option>
                        <option value="ownType">Define own type</option>
                    </select>
                </div>
                <div>
                    <input
                        multiple={ false }
                        onChange={ e => setTask({ ...task, levelUpLimit: Number(e.target.value) }) }
                        value={ task.levelUpLimit }
                        required type="text"
                        placeholder="Set up level up limit"
                    />
                </div>
                <div>
                    <button  className="addTaskBtn" onClick={ onAddTaskBtnClick }>Add task</button>
                </div>

            </form>

            <TasksTable tasks={ tasks }/>
            <p>how much time did you spent today?</p>
            <p>how much time did you spent for the last week?</p>
            <h3>React</h3>
            <p>? как передавать данные через один дочерний компонент</p>
            <p>? как тригерить событие родительского компонента из дочернего</p>
            <p>? как передать данные из дочернего компонента в родительский</p>
            <p>? чем отличается сет стейт от стейта, когда какой лучше юзать</p>
        </div>
    );
}

export default App;
