const SET_USER = "SET_USER";

const defaultState = {
    currentUser: {},
    isAuth: false
};

export const userReducer = (state = defaultState, action) => {
    switch (action.type) {
        case SET_USER:
            return {
                ...state,
                current: action.payload.user,
                isAuth: true
            }
        default:
            return state;
    }
}
