const defaultState = {
    auth: false
}
const SET_AUTH = 'SET_AUTH';

export default function authReducer(state = defaultState, action) {
    switch (action.type) {
        case SET_AUTH:
            return { auth: action.payload };
        default:
            return state;
    }
}
