import React, { useState } from 'react';
import InputDefault from '../UI/input/InputDefault';
import ButtonDefault from '../UI/button/ButtonDefault';
import './SearchTaskForm.css';

const SearchTaskForm = ({ searchTask }) => {
    const [searchQuery, setSearchQuery] = useState('');
    const onSearchBtnClick = (e) => {
        e.preventDefault();
        searchTask(searchQuery);
        setSearchQuery('');
    }
    const onCancelBtnClick = (e) => {
        e.preventDefault();
        searchTask('');
    }

    return (
        <form className="search-task-form">
            <InputDefault
                onChange={ e => setSearchQuery(e.target.value) }
                required type="text"
                placeholder="Enter task name"
                value={ searchQuery }
            />
            <ButtonDefault onClick={ onSearchBtnClick }>Search task</ButtonDefault>
            <ButtonDefault onClick={ onCancelBtnClick }>Cancel</ButtonDefault>
        </form>
    );
};

export default SearchTaskForm;
