const defaultState = {
    tasks: JSON.parse(localStorage.getItem('tasks')) || []
};
export const tasksReducer = (state = defaultState, action) => {
    switch (action.type) {
        case 'ADD_TASK':

            return { ...state, tasks: [...state.tasks, (action.payload)] };
        case 'REMOVE_TASK':
            return { ...state, tasks: action.payload };
        case 'UPDATE_TASK':
            return { ...state, tasks: action.payload };
        default:
            return state;
    }
};
