import React, { useState } from 'react';

function Task({ task }) {
    const [pointsDone, setPointsDone] = useState(task.currentPointsValue);
    const [currentLevel, setCurrentLevel] = useState(task.currentLevel);

    function increment() {

        if (pointsDone === task.levelUpLimit && currentLevel !== task.levelUpType.length - 1) {

            setPointsDone(0);
            setCurrentLevel(currentLevel + 1);

            return;
        } else if (pointsDone === task.levelUpLimit && currentLevel === task.levelUpType.length - 1) {
            return;
        }

        setPointsDone(pointsDone + 1);
    }

    function decrement() {

        if (pointsDone === 0 && currentLevel !== 0) {

            setPointsDone(task.levelUpLimit);
            setCurrentLevel(currentLevel - 1);

            return;
        } else if (pointsDone === 0 && currentLevel === 0) {
            return;
        }

        setPointsDone(pointsDone - 1);
    }

    return (
        <tr className="tasks-table__row">
            <td className="tasks-table__cell">{  }</td>
            <td className="tasks-table__cell">{ task.taskName }</td>
            <td className="tasks-table__cell">
                <span className="tasks-table__cell-points-value">
                    { pointsDone } / { task.levelUpLimit } { task.pointsType }
                </span>
                <button
                    onClick={ increment }
                    className="tasks-table__cell-button"
                >+</button>
                <button
                    onClick={ decrement }
                    className="tasks-table__cell-button"
                >-</button>
            </td>
            <td className="tasks-table__cell">{ task.levelUpType[currentLevel] }</td>
            <td className="tasks-table__cell">
                <button>Done</button>
                <button>Delete</button>
            </td>
        </tr>
    );
}

export default Task;
