import React, { useState } from 'react';

function Task({ task }) {
    const [pointsDone, setPointsDone] = useState(task.currentPointsValue);

    function increment() {
        console.log(task.currentPointsValue);
        setPointsDone(pointsDone + 1);
        console.log(pointsDone);
    }

    function decrement() {
        setPointsDone(pointsDone - 1);
        console.log(pointsDone);
    }

    return (
        <tr className="tasks-table__row">
            <td className="tasks-table__cell">{ task.taskName }</td>
            <td className="tasks-table__cell">
                { task.currentPointsValue } / 10 { task.pointsType }
                <button onClick={ increment }>+</button>
                <button onClick={ decrement }>-</button>
            </td>
            <td className="tasks-table__cell">{ task.currentLevel }</td>
            <td className="tasks-table__cell">
                <button>Edit</button>
                <button>Delete</button>
            </td>
        </tr>
    );
}
/*<p>{ value }{ likes }</p>*/
