import React from 'react';
import { Button } from '@mui/material';

const Task = ({ id, taskName, pointsType, currentLevel }) => {

    function increment() {
        this.setState({ currentPointsValue: this.state.currentPointsValue + 1 });
    }

    function decrement() {
        this.setState({ currentPointsValue: this.state.currentPointsValue - 1 });
    }

    return (
        <tr className="tasks-table__row">
            <td className="tasks-table__cell">{ id }</td>
            <td className="tasks-table__cell">{ taskName }</td>
            <td className="tasks-table__cell">
                { this.props.currentPointsValue } / 10 { pointsType }
                <Button variant="contained" onClick={ increment }>↑</Button>
                <Button variant="outlined" onClick={ decrement }>↓</Button>
            </td>
            <td className="tasks-table__cell">{ currentLevel }</td>
        </tr>
    );
}
