const defaultState = {
    tasks: JSON.parse(localStorage.getItem('tasks')) || []
};
export const tasksReducer = (state = defaultState, action) => {
    let updatedTasksArr = [];

    switch (action.type) {
        case 'ADD_TASK':
            return { ...state, tasks: action.payload };
        case 'REMOVE_TASK':
            const newTasksArr = action.payload.tasksAtStore.filter(task => task.id !== action.payload.taskId);
            localStorage.setItem('tasks', JSON.stringify(newTasksArr));

            return { ...state, tasks: action.payload };
        case 'UPDATE_TASK':
            updatedTasksArr = action.payload.tasksAtStore.map(task => {
                if (task.id === action.payload.taskId) {
                    return { ...task, pointsDone: action.payload.pointsDone, currentLevel: action.payload.currentLevel };
                }
                return task;
            });
            localStorage.setItem('tasks', JSON.stringify(updatedTasksArr));

            return { ...state, tasks: updatedTasksArr };
        case 'COMPLETE_TASK':
            updatedTasksArr = action.payload.tasksAtStore.map(task => {
                if (task.id === action.payload.taskId) {
                    return { ...task, complete: true };
                }
                return task;
            });
            localStorage.setItem('tasks', JSON.stringify(updatedTasksArr));

            return { ...state, tasks: updatedTasksArr };
        default:
            return state;
    }
};
