import React, { useState } from 'react';
import './App.css';
import { Button } from '@mui/material';
import tasksTable from '../tasksTable/TasksTable';

// rsc

function App() {
    const [likes, setLikes] = useState(0);
    const [value, setValue] = useState('Likes: ');

    function increment() {
        setLikes(likes + 1);
    }

    function decrement() {
        setLikes(likes - 1);
    }

    return (
        <div className="App">
            <p>{ value }{ likes }</p>
            <input
                type="text"
                value={ value }
                onChange={ event => setValue(event.target.value) }
            />
            <Button variant="contained" onClick={ increment }>Like</Button>
            <Button variant="outlined" onClick={ decrement }>Dislike</Button>

            <tasksTable />
        </div>
    );
}

export default App;
