import React, { useState } from 'react';
import './App.css';
import { Button } from '@mui/material';
import TasksTable from '../tasksTable/TasksTable';

// rsc

function App() {
    function addTask() {
        const table = document.getElementsByClassName('taskTable');

        table.innerHtml(<TasksTable />);
    }

    return (
        <div className="App">
            <Button variant="contained" onClick={ addTask }>Add task</Button>
            <TasksTable />
        </div>
    );
}

export default App;
