import React, {useState} from 'react';

class Task extends React.Component {

    constructor(props) {
        super(props);
        this.state = {

        }

        this.increment = this.increment.bind(this);
        this.decrement = this.decrement.bind(this);
    }

    increment() {
        this.setState({ currentPointsValue: this.state.currentPointsValue + 1 });
    }

    decrement() {
        this.setState({ currentPointsValue: this.state.currentPointsValue - 1 });
    }

    render() {
        return (
            <tr className="tasks-table__row">
                <td className="tasks-table__cell">{ this.state.id }</td>
                <td className="tasks-table__cell">{ this.state.taskName }</td>
                <td className="tasks-table__cell">
                    { this.state.currentPointsValue } / 10 { this.state.pointsType }
                    <Button variant="contained" onClick={ increment }>↑</Button>
                    <Button variant="outlined" onClick={ decrement }>↓</Button>
                </td>
                <td className="tasks-table__cell">{ this.state.currentLevel }</td>
            </tr>
        );
    }
}
