import React from 'react';
import { Button } from '@mui/material';

function Task(task) {

    function increment() {
        // this.setState({ currentPointsValue: this.state.currentPointsValue + 1 });
    }

    function decrement() {
        // this.setState({ currentPointsValue: this.state.currentPointsValue - 1 });
    }

    return (
        <tr className="tasks-table__row">
            <td className="tasks-table__cell">{ task.task.id }</td>
            <td className="tasks-table__cell">{ task.taskName }</td>
            <td className="tasks-table__cell">
                { task.currentPointsValue } / 10 { task.pointsType }
                <Button variant="contained" onClick={ increment }>↑</Button>
                <Button variant="outlined" onClick={ decrement }>↓</Button>
            </td>
            <td className="tasks-table__cell">{ task.currentLevel }</td>
        </tr>
    );
}
