import React, {
    useMemo,
    useState,
    useEffect
} from 'react';
import './App.css';
import TasksTable from '../tasksTable/TasksTable.jsx';
import AddTaskForm from '../addTaskForm/AddTaskForm';
import ButtonDefault from '../UI/button/ButtonDefault';
import SearchTaskForm from '../searchTaskForm/SearchTaskForm';
import Modal from '../modal/Modal';
import { useDispatch, useSelector } from "react-redux";

function App() {
    const dispatch = useDispatch();
    const tasksAtStore = useSelector(state => state.tasks.tasks);
    const isAddTaskModalVisible = useSelector(state => state.addTaskModal.isVisible);


    // const [tasks, setTasks] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');

    const searchQueryCallback = (taskNameToSearch) => setSearchQuery(taskNameToSearch);

    const foundTasks = useMemo(() => {
        if (searchQuery) {
            // let regexp = new RegExp(searchQuery, 'gi');

            // return tasks.filter((tasksItem) => regexp.test(tasksItem.taskName));
            // const filteredTasks = tasksAtStore.filter((tasksItem) => regexp.test(tasksItem.taskName));
            dispatch({ type: 'ADD_TASK', payload: {searchQuery, tasksAtStore} });
        }

        return tasksAtStore;
    }, [searchQuery, tasksAtStore]);

    return (
        <div className="app">
            <Modal visible={ isAddTaskModalVisible } >
                <AddTaskForm />
            </Modal>
            <div className="app__heading-actions-container">
                <ButtonDefault onClick={ () => dispatch({ type: 'SHOW_MODAL', payload: true }) } >Create task</ButtonDefault>
                <SearchTaskForm searchTask={ searchQueryCallback } />
                <SearchTaskForm />
            </div>
            <TasksTable
                tasks={ foundTasks }
                // removeTask={ removeTask }
                // completeTask={ completeTask }
                // updateTask={ updateTask }
            />
        </div>
    );
}

export default App;
