import React, { useState } from 'react';
import './App.css';
import { Button } from '@mui/material';
import TasksTable from '../tasksTable/TasksTable';

// rsc

function App() {
    const [tasks, setTasks] = useState([
        { id: 1, taskName: 'some Name', pointsType: 'hours', currentPointsValue: 1, currentLevel: 'Junior' },
        { id: 2, taskName: 'exact Name', pointsType: 'points', currentPointsValue: 5, currentLevel: 'Middle' }
    ]);

    function onAddTaskBtnClick() {
