import React, { useState } from 'react';
import './App.css';
import TasksTable from '../tasksTable/TasksTable';

// rsc

function App() {
    const [tasks, setTasks] = useState([
        { id: 1, taskName: 'some Name', pointsType: 'hours', currentPointsValue: 1, currentLevel: 'Junior' },
        { id: 2, taskName: 'exact Name', pointsType: 'points', currentPointsValue: 5, currentLevel: 'Middle' }
    ]);
    const [taskName, setTaskName] = useState('');
    const [pointsType,setPointsType] = useState('');
    const [levelUpType, setLevelUpType] = useState('');

    const onAddTaskBtnClick = (e) => {
        e.preventDefault();
        console.log(levelUpType);

        const newTask = {
            id: Math.round(Math.random(0, 101) * 100),
            taskName,
            pointsType,
            currentPointsValue: 0,
            currentLevel: levelUpType === 'it' ? 'Junior' : 0
        }
console.log(newTask);

        return '';
    }

    return (
        <div className="App">
            <form>
                <div>
                    <input onChange={ e => setTaskName(e.target.value) } value={ taskName } required type="text" placeholder="Task name" />
                </div>
                <div>
                    <select
                        required
                        size="1"
                        name="pointsType"
                        onChange={ e => setPointsType(e.target.value) } value={ pointsType }
                    >
                        <option selected disabled>Choose points type</option>
                        <option value="hours">hours</option>
                        <option value="points">points</option>
                        <option value="counter">counter</option>
                    </select>
                </div>
                <div>
                    <select
                        required
                        size="1"
                        name="pointsType"
                        onChange={ e => setLevelUpType(e.target.value) } value={ levelUpType }
                    >
                        <option disabled>Choose level up type</option>
                        <option value="it">Trainee, Junior, Middle, Senior</option>
                        <option value="points">Numbers counter</option>
                        <option value="counter">Define own type</option>
                    </select>
                </div>
                <div>
                    <button  className="addTaskBtn" onClick={ onAddTaskBtnClick }>Add task</button>
                </div>

            </form>

            <TasksTable tasks={ tasks }/>
            <p>how much time did you spent today?</p>
            <p>how much time did you spent for the last week?</p>
            <h3>React</h3>
            <p>? как передавать данные через один дочерний компонент</p>
            <p>? как тригерить событие родительского компонента из дочернего</p>
            <p>? как передать данные из дочернего компонента в родительский</p>
            <p>? чем отличается сет стейт от стейта, когда какой лучше юзать</p>
        </div>
    );
}

export default App;
