import React from 'react';
import './TasksTable.css';
import Task from '../task/Task';

class TasksTable extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            tasksList: this.props
        }
    }

    render() {
        const tasks = this.state.tasksList;

        return (
            <div className="tasks-table-container">
                <table className="tasks-table">
                    <thead>
                    <tr>
                        <th className="tasks-table__cell">#</th>
                        <th className="tasks-table__cell">Task</th>
                        <th className="tasks-table__cell">Points done</th>
                        <th className="tasks-table__cell">Points left</th>
                    </tr>
                    </thead>
                    <tbody>
                        { tasks.map(task => <Task key={task.id} {...task} />) }
                    </tbody>
                </table>
            </div>
        );
    }
}

export default TasksTable;
