import React, { useState } from 'react';
import ButtonDefault from '../UI/button/ButtonDefault';
import InputDefault from "../UI/input/InputDefault";
import "./TaskForm.css";

const TaskForm = ({ create, remove }) => {
    const [task, setTask] = useState({
        taskName: '',
        pointsType: 'Choose points type',
        currentPointsValue: 0,
        levelUpType: 'Choose level up type',
        levelUpLimit: '',
        currentLevel: 0
    });
    const onAddTaskBtnClick = (e) => {
        e.preventDefault();
        const newTask = {
            ...task, id: Date.now()
        };
        create(newTask);
        setTask({
            taskName: '',
            pointsType: 'Choose points type',
            currentPointsValue: 0,
            levelUpType: 'Choose level up type',
            levelUpLimit: '',
            currentLevel: 0
        });
    }
    const levelUpTypeCheck = (levelUpType) => {
        switch (levelUpType) {
            case 'it':
                return ['Trainee', 'Junior', 'Middle', 'Senior'];
            case 'cabaliero':
                return ['Junipero', 'Midelero', 'Senioro'];
            case 'shaolin':
                return ['Salobon', 'Master'];
            case 'numbersCounter':
                const numbersArr = [];

                for (let i = 0; i <= 100; i++) {
                    numbersArr.push(i);
                }

                return numbersArr;
            case 'ownType':
                return ['own type'];
        }
    }

    return (
        <form className="add-task-form">
            <InputDefault
                onChange={ e => setTask({ ...task, taskName: e.target.value }) }
                value={ task.taskName }
                required
                type="text"
                placeholder="Task name"
            />
            <select
                required
                size="1"
                name="pointsType"
                onChange={ e => setTask({ ...task, pointsType: e.target.value }) }
                value={ task.pointsType }
                className="add-task-form-select"
            >
                <option disabled>Choose points type</option>
                <option value="hours">hours</option>
                <option value="points">points</option>
                <option value="counter">counter</option>
            </select>
            <select
                required
                size="1"
                name="levelUpType"
                onChange={ e => setTask({ ...task, levelUpType: levelUpTypeCheck(e.target.value) }) }
                value={ task.levelUpType }
                className="add-task-form-select"
            >
                <option disabled>Choose level up type</option>
                <option value="it">Trainee, Junior, Middle, Senior</option>
                <option value="cabaliero">Junipero, Midelero, Senioro</option>
                <option value="shaolin">Salobon, Master</option>
                <option value="numbersCounter">Numbers counter</option>
                <option value="ownType">Define own type</option>
            </select>
            <InputDefault
                onChange={ e => setTask({ ...task, levelUpLimit: Number(e.target.value) }) }
                value={ task.levelUpLimit }
                required type="text"
                placeholder="Set up level up limit"
            />
            <ButtonDefault onClick={ onAddTaskBtnClick }>Add task</ButtonDefault>
        </form>
    );
};

export default TaskForm;
