import React from 'react';
import './App.css';
import { Button } from '@mui/material';
import TasksTable from '../tasksTable/TasksTable';
import Task from '../task/Task';

// rsc

class App extends React.Component {
    constructor() {
        super();
        this.state = {
            tasksList: []
        }

        this.addTask = this.addTask.bind(this);
    }

    onAddTaskBtnClick() {
        const tasksList = this.state.tasksList;
        this.setState({
            tasksList: tasksList.concat(
                <Task
                    id={Math.random(0, 101)}
                    taskName={'some Name'}
                    pointsType={'hours'}
                    currentPointsValue={1}
                    currentLevel={'Junior'}
                    key={tasksList.length}
                />)
        });
    }

    render() {
        return (
            <div className="App">
                <Button variant="contained" onClick={ this.onAddTaskBtnClick }>Add task</Button>
                <TasksTable tasksList={ this.state.tasksList }/>
            </div>
        );
    }
}

export default App;
