import React from 'react';

function Task({ task }) {

    function increment() {
        // this.setState({ currentPointsValue: this.state.currentPointsValue + 1 });
    }

    function decrement() {
        // this.setState({ currentPointsValue: this.state.currentPointsValue - 1 });
    }

    return (
        <tr className="tasks-table__row">
            <td className="tasks-table__cell">{ task.taskName }</td>
            <td className="tasks-table__cell">
                { task.task.currentPointsValue } / 10 { task.pointsType }
                <button onClick={ increment }>+</button>
                <button onClick={ decrement }>-</button>
            </td>
            <td className="tasks-table__cell">{ task.currentLevel }</td>
            <td className="tasks-table__cell">
                <button>Edit</button>
                <button>Delete</button>
            </td>
        </tr>
    );
}
